import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

// Allow graceful fallback for development without database
if (!process.env.DATABASE_URL) {
  console.warn(
    "âš ï¸ DATABASE_URL not set. Using fallback mode. Some features may not work correctly."
  );
}

// Only create pool and db if DATABASE_URL is available
export const pool = process.env.DATABASE_URL ? 
  new Pool({ connectionString: process.env.DATABASE_URL }) : 
  null;

export const db = pool ? 
  drizzle({ client: pool, schema }) : 
  null;

// Helper to check if database is available
export const isDatabaseAvailable = () => !!process.env.DATABASE_URL;

