import type { Express } from "express";
import { createServer, type Server } from "http";
import session from "express-session";
import connectPgSimple from "connect-pg-simple";
import { storage } from "./storage";
import { z } from "zod";
import { 
  insertInvoiceSchema, 
  insertClientProviderSchema,
  fiscalPeriodQuerySchema,
  ivaComponentCreateSchema,
  paymentStatusUpdateSchema,
  excelImportSchema,
  userConfigUpdateSchema,
  insertInvoiceTemplateSchema
} from "@shared/schema";
import { invoiceProcessor } from "./ai/invoiceProcessor";
import { pythonAIProxy } from "./python-proxy";
import { azureProcessor } from "./azure-ai-processor";
import { loginUser, requireAuth, requireRole, hashPassword } from "./auth";
import multer from "multer";
import path from "path";
import fs from "fs";
import { Pool } from "pg";
import * as XLSX from "xlsx";
import { wsManager } from "./websocket";
import { uploadJobManager, UploadJobManager } from "./uploadJobManager";

// Configure multer for file uploads
const uploadDir = path.join(process.cwd(), 'uploads');
if (!fs.existsSync(uploadDir)) {
  fs.mkdirSync(uploadDir, { recursive: true });
}

const upload = multer({
  dest: uploadDir,
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB
  },
  fileFilter: (req, file, cb) => {
    const allowedTypes = ['image/jpeg', 'image/png', 'application/pdf'];
    if (allowedTypes.includes(file.mimetype)) {
      cb(null, true);
    } else {
      cb(null, false);
    }
  },
});

// Configure multer for Excel file uploads
const excelUpload = multer({
  dest: uploadDir,
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB
  },
  fileFilter: (req, file, cb) => {
    const allowedTypes = [
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet', // .xlsx
      'application/vnd.ms-excel', // .xls
      'application/octet-stream' // Sometimes Excel files are detected as this
    ];
    if (allowedTypes.includes(file.mimetype)) {
      cb(null, true);
    } else {
      cb(null, false);
    }
  },
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Setup session middleware with fallback to memory store for development
  let sessionStore;

  if (process.env.DATABASE_URL) {
    // Use PostgreSQL session store in production
    const pgSession = connectPgSimple(session);
    const sessionPool = new Pool({
      connectionString: process.env.DATABASE_URL,
    });
    sessionStore = new pgSession({
      pool: sessionPool,
      createTableIfMissing: true,
    });
  } else {
    // Use memory store in development
    console.warn("âš ï¸ Using memory session store - sessions will not persist across server restarts");
    sessionStore = new session.MemoryStore();
  }

  app.use(
    session({
      store: sessionStore,
      secret: process.env.SESSION_SECRET || 'opendoors-secret-key-change-this-in-production',
      resave: false,
      saveUninitialized: false,
      cookie: {
        secure: process.env.NODE_ENV === 'production',
        httpOnly: true,
        maxAge: 30 * 24 * 60 * 60 * 1000, // 30 days
      },
    })
  );

  // Authentication Routes
  app.post("/api/auth/register", async (req, res) => {
    try {
      const { username, displayName, email, password } = req.body;

      // Validate required fields
      if (!username || !displayName || !email || !password) {
        return res.status(400).json({ 
          message: "Todos los campos son requeridos" 
        });
      }

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(email);
      if (existingUser) {
        return res.status(400).json({ 
          message: "Este email ya estÃ¡ registrado" 
        });
      }

      const existingUsername = await storage.getUserByUsername(username);
      if (existingUsername) {
        return res.status(400).json({ 
          message: "Este nombre de usuario ya estÃ¡ en uso" 
        });
      }

      // Hash password and create user
      const hashedPassword = await hashPassword(password);
      const newUser = await storage.createUser({
        username,
        displayName,
        email,
        password: hashedPassword,
        role: 'viewer', // Default role for new users
        isActive: true,
        fiscalPeriod: 'calendar',
      });

      // Log registration activity
      await storage.createActivityLog({
        userId: newUser.id,
        userName: newUser.displayName,
        actionType: 'create',
        entityType: 'user',
        entityId: newUser.id,
        description: `Nuevo usuario registrado: ${newUser.displayName}`,
        metadata: JSON.stringify({ email: newUser.email }),
        ipAddress: req.ip || req.connection.remoteAddress
      });

      res.status(201).json({
        message: "Registro exitoso",
        user: {
          id: newUser.id,
          displayName: newUser.displayName,
          email: newUser.email,
          role: newUser.role,
        }
      });
    } catch (error) {
      console.error('Registration error:', error);
      res.status(500).json({ message: "Error al registrar usuario" });
    }
  });

  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = req.body;

      if (!email || !password) {
        return res.status(400).json({ message: "Email y contraseÃ±a son requeridos" });
      }

      const ipAddress = req.ip || req.connection.remoteAddress;
      const userSession = await loginUser(email, password, ipAddress);

      if (!userSession) {
        return res.status(401).json({ message: "Credenciales invÃ¡lidas" });
      }

      req.session.user = userSession;
      res.json({
        user: userSession,
        message: "Inicio de sesiÃ³n exitoso"
      });
    } catch (error) {
      console.error('Login error:', error);
      res.status(500).json({ message: "Error al iniciar sesiÃ³n" });
    }
  });

  app.post("/api/auth/logout", requireAuth, async (req, res) => {
    try {
      const user = req.session.user;
      if (user) {
        await storage.createActivityLog({
          userId: user.id,
          userName: user.displayName,
          actionType: 'logout',
          entityType: 'user',
          entityId: user.id,
          description: `User ${user.displayName} logged out`,
          metadata: JSON.stringify({ email: user.email }),
          ipAddress: req.ip || req.connection.remoteAddress
        });
      }

      req.session.destroy((err) => {
        if (err) {
          console.error('Logout error:', err);
          return res.status(500).json({ message: "Error al cerrar sesiÃ³n" });
        }
        res.json({ message: "SesiÃ³n cerrada exitosamente" });
      });
    } catch (error) {
      console.error('Logout error:', error);
      res.status(500).json({ message: "Error al cerrar sesiÃ³n" });
    }
  });

  app.get("/api/auth/me", requireAuth, async (req, res) => {
    try {
      // Fetch fresh user data from database instead of returning cached session
      const userId = req.session.user!.id;
      const freshUser = await storage.getUser(userId);

      if (!freshUser) {
        return res.status(404).json({ error: "Usuario no encontrado" });
      }

      // Update session with fresh data including configuration
      req.session.user = {
        id: freshUser.id,
        displayName: freshUser.displayName,
        email: freshUser.email,
        role: freshUser.role,
        avatar: freshUser.avatar,
        decimalSeparator: freshUser.decimalSeparator,
        thousandSeparator: freshUser.thousandSeparator,
        decimalPlaces: freshUser.decimalPlaces,
        currencySymbol: freshUser.currencySymbol,
        currencyPosition: freshUser.currencyPosition,
        roundingMode: freshUser.roundingMode,
        fiscalPeriod: freshUser.fiscalPeriod,
      };

      res.json({ user: req.session.user });
    } catch (error) {
      console.error('Error fetching user:', error);
      res.status(500).json({ error: "Error al obtener informaciÃ³n del usuario" });
    }
  });

  // User Management (admin only)
  app.get("/api/users", requireAuth, requireRole('admin'), async (req, res) => {
    try {
      const users = await storage.getAllUsers();
      res.json(users);
    } catch (error) {
      console.error('Error fetching users:', error);
      res.status(500).json({ error: "Error al obtener usuarios" });
    }
  });

  app.post("/api/users", requireAuth, requireRole('admin'), async (req, res) => {
    try {
      const { username, displayName, email, password, role } = req.body;

      if (!username || !displayName || !email || !password) {
        return res.status(400).json({ error: "Todos los campos son requeridos" });
      }

      // Check if user exists
      const existingUser = await storage.getUserByEmail(email);
      if (existingUser) {
        return res.status(409).json({ error: "El email ya estÃ¡ registrado" });
      }

      const hashedPassword = await hashPassword(password);
      const user = await storage.createUser({
        username,
        displayName,
        email,
        password: hashedPassword,
        role: role || 'viewer',
        isActive: true,
      });

      // Log activity
      await storage.createActivityLog({
        userId: req.session.user!.id,
        userName: req.session.user!.displayName,
        actionType: 'create',
        entityType: 'user',
        entityId: user.id,
        description: `CreÃ³ usuario ${user.displayName} (${user.email})`,
        metadata: JSON.stringify({ role: user.role }),
        ipAddress: req.ip,
      });

      res.json(user);
    } catch (error) {
      console.error('Error creating user:', error);
      res.status(500).json({ error: "Error al crear usuario" });
    }
  });

  app.put("/api/users/:id", requireAuth, requireRole('admin'), async (req, res) => {
    try {
      const { displayName, email, role, isActive } = req.body;
      const userId = req.params.id;

      const updates: any = {};
      if (displayName !== undefined) updates.displayName = displayName;
      if (email !== undefined) updates.email = email;
      if (role !== undefined) updates.role = role;
      if (isActive !== undefined) updates.isActive = isActive;

      const user = await storage.updateUser(userId, updates);

      if (!user) {
        return res.status(404).json({ error: "Usuario no encontrado" });
      }

      // Log activity
      await storage.createActivityLog({
        userId: req.session.user!.id,
        userName: req.session.user!.displayName,
        actionType: 'update',
        entityType: 'user',
        entityId: userId,
        description: `ActualizÃ³ usuario ${user.displayName}`,
        metadata: JSON.stringify(updates),
        ipAddress: req.ip,
      });

      res.json(user);
    } catch (error) {
      console.error('Error updating user:', error);
      res.status(500).json({ error: "Error al actualizar usuario" });
    }
  });

  app.delete("/api/users/:id", requireAuth, requireRole('admin'), async (req, res) => {
    try {
      const userId = req.params.id;

      // Prevent self-deletion
      if (userId === req.session.user!.id) {
        return res.status(400).json({ error: "No puedes eliminar tu propio usuario" });
      }

      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ error: "Usuario no encontrado" });
      }

      const success = await storage.deleteUser(userId);

      if (success) {
        // Log activity
        await storage.createActivityLog({
          userId: req.session.user!.id,
          userName: req.session.user!.displayName,
          actionType: 'delete',
          entityType: 'user',
          entityId: userId,
          description: `EliminÃ³ usuario ${user.displayName}`,
          metadata: JSON.stringify({ deletedUser: user.email }),
          ipAddress: req.ip,
        });

        res.json({ success: true, message: "Usuario eliminado" });
      } else {
        res.status(500).json({ error: "Error al eliminar usuario" });
      }
    } catch (error) {
      console.error('Error deleting user:', error);
      res.status(500).json({ error: "Error al eliminar usuario" });
    }
  });

  // Profile Management (current user)
  app.put("/api/auth/profile", requireAuth, async (req, res) => {
    try {
      const { name, email } = req.body;
      const userId = req.session.user!.id;

      const updates: any = {};
      if (name !== undefined) updates.displayName = name;
      if (email !== undefined) updates.email = email;

      const user = await storage.updateUser(userId, updates);

      if (!user) {
        return res.status(404).json({ error: "Usuario no encontrado" });
      }

      // Update session
      req.session.user = {
        ...req.session.user!,
        displayName: user.displayName,
        email: user.email,
      };

      res.json({ message: "Perfil actualizado", user });
    } catch (error) {
      console.error('Error updating profile:', error);
      res.status(500).json({ error: "Error al actualizar perfil" });
    }
  });

  app.post("/api/auth/change-password", requireAuth, async (req, res) => {
    try {
      const { currentPassword, newPassword } = req.body;
      const userId = req.session.user!.id;

      if (!currentPassword || !newPassword) {
        return res.status(400).json({ error: "Se requieren ambas contraseÃ±as" });
      }

      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ error: "Usuario no encontrado" });
      }

      // Verify current password
      const bcrypt = require('bcrypt');
      const isValid = await bcrypt.compare(currentPassword, user.password);

      if (!isValid) {
        return res.status(401).json({ error: "ContraseÃ±a actual incorrecta" });
      }

      // Hash and update new password
      const hashedPassword = await hashPassword(newPassword);
      await storage.updateUserPassword(userId, hashedPassword);

      res.json({ message: "ContraseÃ±a actualizada exitosamente" });
    } catch (error) {
      console.error('Error changing password:', error);
      res.status(500).json({ error: "Error al cambiar contraseÃ±a" });
    }
  });

  app.put("/api/auth/configuration", requireAuth, async (req, res) => {
    try {
      const userId = req.session.user!.id;

      // Validate and parse configuration using Zod schema
      const validatedConfig = userConfigUpdateSchema.parse(req.body);

      const updatedUser = await storage.updateUserConfiguration(userId, validatedConfig);

      if (!updatedUser) {
        return res.status(404).json({ error: "Usuario no encontrado" });
      }

      // Update session with fresh user data from database
      req.session.user = {
        id: updatedUser.id,
        displayName: updatedUser.displayName,
        email: updatedUser.email,
        role: updatedUser.role,
        avatar: updatedUser.avatar,
      };

      res.json({ 
        message: "ConfiguraciÃ³n actualizada exitosamente", 
        configuration: {
          decimalSeparator: updatedUser.decimalSeparator,
          thousandSeparator: updatedUser.thousandSeparator,
          decimalPlaces: updatedUser.decimalPlaces,
          currencySymbol: updatedUser.currencySymbol,
          currencyPosition: updatedUser.currencyPosition,
          roundingMode: updatedUser.roundingMode,
          fiscalPeriod: updatedUser.fiscalPeriod,
        }
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ 
          error: "Datos de configuraciÃ³n invÃ¡lidos",
          details: error.errors[0].message
        });
      }
      console.error('Error updating configuration:', error);
      res.status(500).json({ error: "Error al actualizar configuraciÃ³n" });
    }
  });

  // Avatar upload endpoint
  app.post("/api/auth/avatar", requireAuth, upload.single('avatar'), async (req, res) => {
    try {
      const userId = req.session.user!.id;

      if (!req.file) {
        return res.status(400).json({ error: "No se proporcionÃ³ archivo" });
      }

      // Generate unique filename
      const timestamp = Date.now();
      const ext = path.extname(req.file.originalname);
      const filename = `avatar-${userId}-${timestamp}${ext}`;
      const avatarPath = path.join('uploads', 'avatars', filename);
      const fullPath = path.join(uploadDir, 'avatars', filename);

      // Create avatars directory if it doesn't exist
      const avatarsDir = path.join(uploadDir, 'avatars');
      if (!fs.existsSync(avatarsDir)) {
        fs.mkdirSync(avatarsDir, { recursive: true });
      }

      // Move file to avatars directory
      fs.renameSync(req.file.path, fullPath);

      // Update user avatar in database
      await storage.updateUserAvatar(userId, `/uploads/avatars/${filename}`);

      // Fetch the updated user from database to get fresh data
      const updatedUser = await storage.getUser(userId);

