@import url('https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap');
@import './styles/dialog-overrides.css';
@import './styles/color-updates.css';
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: hsl(0, 0%, 100%);
  --foreground: hsl(222.2, 84%, 4.9%);
  --card: hsl(0, 0%, 100%);
  --card-foreground: hsl(222.2, 84%, 4.9%);
  --popover: hsl(0, 0%, 100%);
  --popover-foreground: hsl(222.2, 84%, 4.9%);
  --primary: hsl(221.2, 83.2%, 53.3%);
  --primary-foreground: hsl(210, 40%, 98%);
  --secondary: hsl(210, 40%, 96%);
  --secondary-foreground: hsl(222.2, 84%, 4.9%);
  --muted: hsl(210, 40%, 96%);
  --muted-foreground: hsl(215.4, 16.3%, 46.9%);
  --accent: hsl(210, 40%, 96%);
  --accent-foreground: hsl(222.2, 84%, 4.9%);
  --destructive: hsl(0, 84.2%, 60.2%);
  --destructive-foreground: hsl(210, 40%, 98%);
  --border: hsl(214.3, 31.8%, 91.4%);
  --input: hsl(214.3, 31.8%, 91.4%);
  --ring: hsl(221.2, 83.2%, 53.3%);
  --radius: 0.5rem;
  --chart-1: hsl(12, 76%, 61%);
  --chart-2: hsl(173, 58%, 39%);
  --chart-3: hsl(197, 37%, 24%);
  --chart-4: hsl(43, 74%, 66%);
  --chart-5: hsl(27, 87%, 67%);

  /* Financial specific colors */
  --income-green: hsl(142, 70%, 40%);
  --expense-red: hsl(0, 72%, 55%);
  --financial-blue: hsl(217, 91%, 95%);
  --financial-blue-dark: hsl(221, 83%, 53%);

  --font-sans: 'Inter', system-ui, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
}

.dark {
  /* Subtle dark mode with transparency and blue tones */
  --background: hsl(220, 15%, 8%);
  --foreground: hsl(210, 20%, 92%);
  --card: hsl(220, 20%, 12%);
  --card-foreground: hsl(210, 15%, 88%);
  --popover: hsl(220, 18%, 10%);
  --popover-foreground: hsl(210, 20%, 90%);
  --primary: hsl(210, 85%, 60%);
  --primary-foreground: hsl(0, 0%, 100%);
  --secondary: hsl(220, 15%, 18%);
  --secondary-foreground: hsl(210, 15%, 85%);
  --muted: hsl(220, 20%, 14%);
  --muted-foreground: hsl(215, 10%, 55%);
  --accent: hsl(220, 25%, 16%);
  --accent-foreground: hsl(210, 80%, 65%);
  --destructive: hsl(0, 65%, 60%);
  --destructive-foreground: hsl(0, 0%, 100%);
  --border: hsl(220, 15%, 20%);
  --input: hsl(220, 20%, 16%);
  --ring: hsl(210, 80%, 60%);
  --chart-1: hsl(210, 85%, 65%);
  --chart-2: hsl(142, 75%, 55%);
  --chart-3: hsl(45, 85%, 65%);
  --chart-4: hsl(142, 70%, 50%);
  --chart-5: hsl(340, 70%, 65%);

  /* Financial specific colors for dark mode - maintain readability */
  --income-green: hsl(142, 70%, 55%);
  --expense-red: hsl(0, 70%, 65%);
  --financial-blue: hsl(217, 60%, 25%);
  --financial-blue-dark: hsl(210, 80%, 60%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', system-ui, sans-serif;
    overflow-x: hidden;
  }

  html {
    overflow-x: hidden;
  }

  /* Global min-width prevention for text overflow */
  .min-w-0 {
    min-width: 0;
  }
}

@layer components {
  .sidebar-transition {
    transition: all 0.3s ease-in-out;
  }

  .card-hover {
    transition: all 0.2s ease-in-out;
  }

  .card-hover:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 25px -5px rgba(0, 0, 0, 0.1);
  }

  /* Mobile-first container defaults */
  .container-mobile {
    @apply max-w-screen-xl mx-auto px-4 sm:px-6 lg:px-8;
  }

  /* Text clamp utilities */
  .text-clamp-1 {
    display: -webkit-box;
    -webkit-line-clamp: 1;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  .text-clamp-2 {
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  .text-clamp-3 {
    display: -webkit-box;
    -webkit-line-clamp: 3;
    -webkit-box-orient: vertical;
    overflow: hidden;
  }

  /* No scrollbar utility */
  .no-scrollbar {
    -ms-overflow-style: none;
    scrollbar-width: none;
  }

  .no-scrollbar::-webkit-scrollbar {
    display: none;
  }

  /* Safe area padding bottom */
  .pb-safe {
    padding-bottom: env(safe-area-inset-bottom);
  }

  /* Sticky toolbar utilities */
  .sticky-toolbar {
    position: sticky;
    bottom: 0;
    z-index: 40;
    background: hsl(var(--background));
    border-top: 1px solid hsl(var(--border));
    padding: env(safe-area-inset-bottom) 1rem 1rem;
  }

  /* Safe area utilities */
  .safe-top {
    padding-top: env(safe-area-inset-top);
  }

  .safe-bottom {
    padding-bottom: env(safe-area-inset-bottom);
  }

  .safe-left {
    padding-left: env(safe-area-inset-left);
  }

  .safe-right {
    padding-right: env(safe-area-inset-right);
  }

  /* Table row styles with alternating colors */
  .table-row-hover {
    transition: background-color 0.2s ease;
  }

  .table-row-hover:nth-child(even) {
    background-color: hsla(207, 90%, 54%, 0.03); /* Very subtle blue with transparency */
  }

  .dark .table-row-hover:nth-child(even) {
    background-color: hsla(207, 90%, 54%, 0.05);
  }

  .table-row-hover:hover {
    background-color: hsla(207, 90%, 54%, 0.08) !important;
  }

  .dark .table-row-hover:hover {
    background-color: hsla(207, 90%, 54%, 0.12) !important;
  }

  .drag-zone {
    transition: all 0.3s ease;
  }

  .drag-zone.drag-over {
    background-color: hsl(var(--financial-blue));
