                  data-testid="edit-iva-input"
                  placeholder="0.00"
                  required
                />
              </div>

              <div className="form-field">
                <Label htmlFor="totalAmount">Total</Label>
                <Input
                  id="totalAmount"
                  type="number"
                  step="0.01"
                  value={formData.totalAmount}
                  onChange={(e) => setFormData({ ...formData, totalAmount: e.target.value })}
                  data-testid="edit-total-input"
                  placeholder="0.00"
                  className="font-semibold"
                  required
                />
              </div>
            </div>
          </div>

          {/* Ownership Section */}
          <div className="dialog-form-section">
            <h3 className="text-sm font-semibold text-muted-foreground mb-3 flex items-center gap-1">
              <User className="w-4 h-4" />
              AsignaciÃ³n
            </h3>
            <div className="grid grid-cols-1 sm:grid-cols-2 gap-3 sm:gap-4">
              <div>
                <Label htmlFor="ownerName" className="text-sm font-medium">Propietario (Socio de OpenDoors)</Label>
                <Select
                  value={formData.ownerName}
                  onValueChange={(value) => setFormData({ ...formData, ownerName: value })}
                >
                  <SelectTrigger data-testid="edit-owner-select">
                    <SelectValue placeholder="Seleccionar propietario..." />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="Joni">Joni</SelectItem>
                    <SelectItem value="HernÃ¡n">HernÃ¡n</SelectItem>
                    <SelectItem value="Otro">Otro (especificar)</SelectItem>
                  </SelectContent>
                </Select>
                {formData.ownerName === 'Otro' && (
                  <Input
                    className="mt-2"
                    type="text"
                    placeholder="Ingrese nombre del propietario"
                    value={customOwner}
                    onChange={(e) => setCustomOwner(e.target.value)}
                    data-testid="edit-custom-owner-input"
                    required
                  />
                )}
              </div>

              <div className="form-field">
                <Label htmlFor="uploadedByName">Usuario que cargÃ³</Label>
                <Select
                  value={formData.uploadedByName}
                  onValueChange={(value) => setFormData({ ...formData, uploadedByName: value })}
                >
                  <SelectTrigger data-testid="edit-uploaded-by-select">
                    <SelectValue placeholder="Seleccionar usuario..." />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="Joni Tagua">Joni Tagua</SelectItem>
                    <SelectItem value="HernÃ¡n Pagani">HernÃ¡n Pagani</SelectItem>
                    <SelectItem value="Otro">Otro</SelectItem>
                  </SelectContent>
                </Select>
                {formData.uploadedByName === 'Otro' && (
                  <Input
                    className="mt-2"
                    type="text"
                    placeholder="Ingrese nombre del usuario"
                    value={customUploadedBy}
                    onChange={(e) => setCustomUploadedBy(e.target.value)}
                    data-testid="edit-other-user-input"
                    required
                  />
                )}
              </div>
            </div>
          </div>

          <div className="flex flex-col-reverse sm:flex-row justify-end gap-2 sm:gap-3 pt-4">
            <Button
              type="button"
              variant="outline"
              onClick={handleClose}
              className="w-full sm:w-auto h-10"
              data-testid="cancel-edit"
            >
              Cancelar
            </Button>
            <Button
              type="submit"
              disabled={updateInvoiceMutation.isPending}
              className="w-full sm:w-auto h-10"
              data-testid="save-invoice"
            >
              {updateInvoiceMutation.isPending ? "Guardando..." : "Guardar Cambios"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/Tables/ClientsTabl.content:
import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Checkbox } from "@/components/ui/checkbox";
import { Edit, Trash2, Plus, Download, FileDown, FileText } from "lucide-react";
import { formatDate, formatCurrency, formatCUIT } from "@/lib/utils";
import { useDeleteClient } from "@/hooks/useClients";
import DeleteConfirmModal from "@/components/Modals/DeleteConfirmModal";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import type { ClientProvider } from "@shared/schema";

interface ClientsTableProps {
  clients: ClientProvider[];
  onEdit: (client: ClientProvider) => void;
  onAdd: () => void;
}

export default function ClientsTable({ clients, onEdit, onAdd }: ClientsTableProps) {
  const [deletingClient, setDeletingClient] = useState<ClientProvider | null>(null);
  const [selectedClients, setSelectedClients] = useState<Set<string>>(new Set());
  const [showBulkDeleteConfirm, setShowBulkDeleteConfirm] = useState(false);
  const [isBulkDeleting, setIsBulkDeleting] = useState(false);

  const deleteClientMutation = useDeleteClient();
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  const handleDelete = async () => {
    if (!deletingClient) return;

    try {
      await deleteClientMutation.mutateAsync(deletingClient.id);
      setDeletingClient(null);
    } catch (error) {
      // Error handled by mutation
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedClients(new Set(clients.map(client => client.id)));
    } else {
      setSelectedClients(new Set());
    }
  };

  const handleSelectClient = (clientId: string, checked: boolean) => {
    const newSelection = new Set(selectedClients);
    if (checked) {
      newSelection.add(clientId);
    } else {
      newSelection.delete(clientId);
    }
    setSelectedClients(newSelection);
  };

  const handleViewInvoices = (client: ClientProvider) => {
    // Store the client filter in sessionStorage and navigate to invoices page
    sessionStorage.setItem('invoiceClientFilter', client.name);
    setLocation('/invoices');

    toast({
      title: "Filtrando facturas",
      description: `Mostrando facturas de ${client.name}`,
      variant: "default",
    });
  };

  const handleBulkDelete = () => {
    if (selectedClients.size === 0) return;
    setShowBulkDeleteConfirm(true);
  };

  const confirmBulkDelete = async () => {
    if (selectedClients.size === 0) return;

    setIsBulkDeleting(true);
    const clientIds = Array.from(selectedClients);
    let successCount = 0;
    let failureCount = 0;

    try {
      for (const clientId of clientIds) {
        try {
          await deleteClientMutation.mutateAsync(clientId);
          successCount++;
        } catch (error) {
          failureCount++;
          console.error(`Failed to delete client ${clientId}:`, error);
        }
      }

      if (successCount > 0) {
        toast({
          title: "Ã‰xito",
          description: `Se eliminaron ${successCount} cliente(s) correctamente${failureCount > 0 ? `. ${failureCount} fallaron.` : '.'}`,
        });
      }

      if (failureCount > 0 && successCount === 0) {
        toast({
          title: "Error",
          description: "No se pudieron eliminar los clientes seleccionados",
          variant: "destructive",
        });
      }

      setSelectedClients(new Set());
      setShowBulkDeleteConfirm(false);
    } finally {
      setIsBulkDeleting(false);
    }
  };

  const exportToCSV = () => {
    const selectedClientsList = clients.filter(client => selectedClients.has(client.id));

    const headers = ['Nombre', 'CUIT', 'Tipo', 'Email', 'TelÃ©fono', 'DirecciÃ³n'];
    const rows = selectedClientsList.map(client => [
      client.name,
      client.cuit || '',
      getTypeLabel(client.type),
      client.email || '',
      client.phone || '',
      client.address || ''
    ]);

    const csvContent = [
      headers.join(','),
      ...rows.map(row => row.map(cell => `"${cell}"`).join(','))
    ].join('\n');

    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `clientes_${new Date().toISOString().split('T')[0]}.csv`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);

    toast({
      title: "Ã‰xito",
      description: `Se exportaron ${selectedClientsList.length} cliente(s) a CSV`,
    });
  };

  const getTypeColor = (type: string) => {
    switch (type) {
      case 'client':
        return 'bg-income-green/10 text-income-green';
      case 'provider':
        return 'bg-expense-red/10 text-expense-red';
      case 'both':
        return 'bg-chart-1/10 text-chart-1';
      default:
        return 'bg-muted/10 text-muted-foreground';
    }
  };

  const getTypeLabel = (type: string) => {
    switch (type) {
      case 'client':
        return 'Cliente';
      case 'provider':
        return 'Proveedor';
      case 'both':
        return 'Ambos';
      default:
        return type;
    }
  };

  return (
    <>
      <Card data-testid="clients-table">
        <div className="p-6 border-b border-border">
          <div className="flex items-center justify-between">
            <div>
              <h3 className="text-lg font-semibold text-foreground">
                Clientes y Proveedores
              </h3>
              <p className="text-sm text-muted-foreground">
                {clients.length} contactos registrados
              </p>
            </div>
            <Button onClick={onAdd} data-testid="add-client">
              <Plus className="w-4 h-4 mr-2" />
              Agregar Contacto
            </Button>
          </div>
        </div>

        {selectedClients.size > 0 && (
          <div className="p-4 border-b border-border bg-muted/50">
            <div className="flex items-center justify-between">
              <span className="text-sm text-muted-foreground">
                {selectedClients.size} cliente(s) seleccionado(s)
              </span>
              <div className="flex gap-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={exportToCSV}
                  data-testid="export-csv-button"
                >
                  <Download className="w-4 h-4 mr-2" />
                  Exportar CSV
                </Button>
                <Button
                  variant="destructive"
                  size="sm"
                  onClick={handleBulkDelete}
                  disabled={isBulkDeleting}
                  data-testid="bulk-delete-button"
                >
                  <Trash2 className="w-4 h-4 mr-2" />
                  Eliminar ({selectedClients.size})
                </Button>
              </div>
            </div>
          </div>
        )}

        {clients.length === 0 ? (
          <CardContent className="p-6 text-center text-muted-foreground">
            No hay clientes o proveedores registrados
          </CardContent>
        ) : (
          <div className="mobile-scroll-container">
            <table className="w-full">
              <thead className="bg-muted/50">
                <tr>
                  <th className="px-4 py-3 text-left">
                    <Checkbox
                      checked={clients.length > 0 && selectedClients.size === clients.length}
                      onCheckedChange={handleSelectAll}
                      data-testid="select-all-checkbox"
                    />
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase">
                    Nombre
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase">
                    CUIT
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase">
                    Tipo
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase">
                    Contacto
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase">
                    Ãšltima Factura
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase">
                    Total Operaciones
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-muted-foreground uppercase">
                    Acciones
                  </th>
                </tr>
              </thead>
              <tbody className="divide-y divide-border">
                {clients.map((client) => (
                  <tr
                    key={client.id}
                    className="table-row-hover"
                    data-testid={`client-row-${client.id}`}
                  >
                    <td className="px-4 py-4">
                      <Checkbox
                        checked={selectedClients.has(client.id)}
                        onCheckedChange={(checked) => handleSelectClient(client.id, checked as boolean)}
                        data-testid={`select-client-${client.id}`}
                      />
                    </td>
                    <td className="px-6 py-4">
                      <div className="text-sm font-medium text-foreground">
                        {client.name}
                      </div>
                    </td>
                    <td className="px-6 py-4 text-sm text-foreground">
                      {client.cuit ? formatCUIT(client.cuit) : '-'}
                    </td>
                    <td className="px-6 py-4">
                      <Badge className={getTypeColor(client.type)}>
                        {getTypeLabel(client.type)}
                      </Badge>
                    </td>
                    <td className="px-6 py-4">
                      <div className="text-sm text-foreground">
                        {client.email && (
                          <div>{client.email}</div>
                        )}
                        {client.phone && (
                          <div className="text-muted-foreground">{client.phone}</div>
                        )}
                        {!client.email && !client.phone && '-'}
                      </div>
                    </td>
                    <td className="px-6 py-4 text-sm text-foreground">
                      {client.lastInvoiceDate ? formatDate(client.lastInvoiceDate) : '-'}
                    </td>
                    <td className="px-6 py-4 text-sm font-medium text-foreground">
                      {formatCurrency(client.totalOperations || '0')}
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex items-center gap-2">
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handleViewInvoices(client)}
                          data-testid={`view-invoices-${client.id}`}
                          title="Ver facturas de este cliente"
                        >
                          <FileText className="w-4 h-4 text-blue-600" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => onEdit(client)}
                          data-testid={`edit-client-${client.id}`}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => setDeletingClient(client)}
                          data-testid={`delete-client-${client.id}`}
                        >
                          <Trash2 className="w-4 h-4 text-destructive" />
                        </Button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </Card>

      <DeleteConfirmModal
        isOpen={!!deletingClient}
        onClose={() => setDeletingClient(null)}
        onConfirm={handleDelete}
        title="Confirmar EliminaciÃ³n de Cliente"
        description="Â¿EstÃ¡s seguro de que deseas eliminar este cliente/proveedor? Esta acciÃ³n no se puede deshacer."
        isLoading={deleteClientMutation.isPending}
      />

      <DeleteConfirmModal
        isOpen={showBulkDeleteConfirm}
        onClose={() => setShowBulkDeleteConfirm(false)}
        onConfirm={confirmBulkDelete}
        title="Eliminar Clientes/Proveedores"
        description={`Â¿EstÃ¡ seguro que desea eliminar ${selectedClients.size} cliente(s)/proveedor(es)? Esta acciÃ³n no se puede deshacer.`}
        isLoading={isBulkDeleting}
      />
    </>
  );
}

