              className="placeholder:opacity-0 sm:placeholder:opacity-100"
              aria-label="Buscar"
            />
          </div>

          <div>
            <Label className="text-sm font-medium text-foreground mb-2 block">
              Mes
            </Label>
            <Select
              value={filters.month}
              onValueChange={(value) => onFiltersChange({ ...filters, month: value })}
            >
              <SelectTrigger data-testid="month-select">
                <SelectValue placeholder="Todos los meses" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todos los meses</SelectItem>
                {months.map((month) => (
                  <SelectItem key={month.value} value={month.value}>
                    {month.label}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div>
            <Label className="text-sm font-medium text-foreground mb-2 block">
              AÃ±o
            </Label>
            <Select
              value={filters.year}
              onValueChange={(value) => onFiltersChange({ ...filters, year: value })}
            >
              <SelectTrigger data-testid="year-select">
                <SelectValue placeholder="Todos los aÃ±os" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todos los aÃ±os</SelectItem>
                {years.map((year) => (
                  <SelectItem key={year} value={year.toString()}>
                    {year}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div>
            <Label className="text-sm font-medium text-foreground mb-2 block">
              Propietario
            </Label>
            <Select
              value={filters.user}
              onValueChange={(value) => onFiltersChange({ ...filters, user: value })}
            >
              <SelectTrigger data-testid="user-filter-select">
                <SelectValue placeholder="Todos" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todos</SelectItem>
                <SelectItem value="Joni">Joni</SelectItem>
                <SelectItem value="HernÃ¡n">HernÃ¡n</SelectItem>
                <SelectItem value="Franco">Franco</SelectItem>
                <SelectItem value="Otro">Otro</SelectItem>
              </SelectContent>
            </Select>
          </div>

          <div>
            <Label className="text-sm font-medium text-foreground mb-2 block">
              Tipo
            </Label>
            <Select
              value={filters.type}
              onValueChange={(value) => onFiltersChange({ ...filters, type: value })}
            >
              <SelectTrigger data-testid="type-filter-select">
                <SelectValue placeholder="Todos" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="all">Todos</SelectItem>
                <SelectItem value="income">Ingreso</SelectItem>
                <SelectItem value="expense">Egreso</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </div>

        <div className="flex items-center gap-3 mt-4">
          <Button onClick={onApplyFilters} data-testid="apply-filters">
            Aplicar Filtros
          </Button>
          <Button variant="secondary" onClick={onClearFilters} data-testid="clear-filters">
            Limpiar
          </Button>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/Tables/InvoicesTab.content:
import { useState } from "react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Checkbox } from "@/components/ui/checkbox";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Edit, Trash2, ChevronLeft, ChevronRight, Eye, FileText, ArrowUpDown, ArrowUp, ArrowDown, Download, FileDown, CreditCard, ChevronDown, Clock, CheckCircle2, AlertCircle, XCircle, Mail, MessageSquare, Share2 } from "lucide-react";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { formatDate, formatCurrency, formatCurrencyWithDecimals, formatCurrencyWithConfig, formatCUIT } from "@/lib/utils";
import { useDeleteInvoice } from "@/hooks/useInvoices";
import EditInvoiceModal from "@/components/Modals/EditInvoiceModal";
import DeleteConfirmModal from "@/components/Modals/DeleteConfirmModal";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { PaymentStatus } from "@/components/PaymentStatus";
import { IvaComponents } from "@/components/IvaComponents";
import type { InvoiceWithRelations } from "@shared/schema";

interface InvoicesTableProps {
  invoices: InvoiceWithRelations[];
  total: number;
  currentPage: number;
  pageSize: number;
  onPageChange: (page: number) => void;
  onSortChange?: (sortBy: string, sortOrder: string) => void;
  sortBy?: string;
  sortOrder?: string;
  onRefetch?: () => void;
}

export default function InvoicesTable({ 
  invoices, 
  total, 
  currentPage, 
  pageSize, 
  onPageChange,
  onSortChange,
  sortBy = 'createdAt',
  sortOrder = 'desc',
  onRefetch
}: InvoicesTableProps) {
  const [editingInvoice, setEditingInvoice] = useState<InvoiceWithRelations | null>(null);
  const [deletingInvoice, setDeletingInvoice] = useState<InvoiceWithRelations | null>(null);
  const [selectedInvoices, setSelectedInvoices] = useState<Set<string>>(new Set());
  const [showBulkDeleteConfirm, setShowBulkDeleteConfirm] = useState(false);
  const [isBulkDeleting, setIsBulkDeleting] = useState(false);

  const deleteInvoiceMutation = useDeleteInvoice();
  const { user } = useAuth();
  const { toast } = useToast();

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedInvoices(new Set(invoices.map(inv => inv.id)));
    } else {
      setSelectedInvoices(new Set());
    }
  };

  const handleSelectInvoice = (invoiceId: string, checked: boolean) => {
    const newSelection = new Set(selectedInvoices);
    if (checked) {
      newSelection.add(invoiceId);
    } else {
      newSelection.delete(invoiceId);
    }
    setSelectedInvoices(newSelection);
  };

  const handleBulkDelete = async () => {
    if (selectedInvoices.size === 0) return;
    // Show confirmation modal
    setShowBulkDeleteConfirm(true);
  };

  const confirmBulkDelete = async () => {
    if (selectedInvoices.size === 0 || !user) return;

    setIsBulkDeleting(true);
    const invoiceIds = Array.from(selectedInvoices);
    let successCount = 0;
    let failureCount = 0;

    try {
      // Delete each invoice
      for (const invoiceId of invoiceIds) {
        try {
          await deleteInvoiceMutation.mutateAsync({
            id: invoiceId,
            deletedBy: user.id,
            deletedByName: user.displayName,
          });
          successCount++;
        } catch (error) {
          failureCount++;
          console.error(`Failed to delete invoice ${invoiceId}:`, error);
        }
      }

      // Show result toast
      if (successCount > 0 && failureCount === 0) {
        toast({
          title: "Facturas eliminadas",
          description: `Se eliminaron ${successCount} factura${successCount > 1 ? 's' : ''} exitosamente`,
        });
      } else if (successCount > 0 && failureCount > 0) {
        toast({
          title: "EliminaciÃ³n parcial",
          description: `Se eliminaron ${successCount} factura${successCount > 1 ? 's' : ''}, pero ${failureCount} no se pudo${failureCount > 1 ? 'ron' : ''} eliminar`,
          variant: "destructive",
        });
      } else {
        toast({
          title: "Error al eliminar",
          description: "No se pudieron eliminar las facturas seleccionadas",
          variant: "destructive",
        });
      }

      // Clear selection if at least one was deleted
      if (successCount > 0) {
        setSelectedInvoices(new Set());
      }
    } catch (error) {
      toast({
        title: "Error al eliminar",
        description: "OcurriÃ³ un error al eliminar las facturas",
        variant: "destructive",
      });
    } finally {
      setIsBulkDeleting(false);
      setShowBulkDeleteConfirm(false);
    }
  };

  const handleBulkExport = async () => {
    if (selectedInvoices.size === 0) return;

    try {
      const response = await fetch('/api/export/bulk', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          invoiceIds: Array.from(selectedInvoices),
          format: 'excel'
        })
      });

      if (response.ok) {
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `facturas_seleccionadas_${Date.now()}.csv`;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        window.URL.revokeObjectURL(url);

        // Clear selection after successful export
        setSelectedInvoices(new Set());
      }
    } catch (error) {
      console.error('Error exporting invoices:', error);
    }
  };

  const handleBulkDownloadFiles = async () => {
    if (selectedInvoices.size === 0) return;

    try {
      const response = await fetch('/api/download/bulk-files', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          invoiceIds: Array.from(selectedInvoices)
        })
      });

      if (response.ok) {
        const contentType = response.headers.get('content-type');

        if (contentType && contentType.includes('application/json')) {
          // Handle multi-file response (coming soon message)
          const data = await response.json();
          alert(data.message || data.error);
        } else {
          // Handle single file download
          const blob = await response.blob();
          const url = window.URL.createObjectURL(blob);
          const a = document.createElement('a');
          a.href = url;
          const contentDisposition = response.headers.get('Content-Disposition');
          const filename = contentDisposition?.split('filename="')[1]?.split('"')[0] || 'factura.pdf';
          a.download = filename;
          document.body.appendChild(a);
          a.click();
          document.body.removeChild(a);
          window.URL.revokeObjectURL(url);
        }
      }
    } catch (error) {
      console.error('Error downloading files:', error);
    }
  };

  const handleBulkPaymentStatus = async (status: 'pending' | 'paid' | 'overdue' | 'cancelled') => {
    if (selectedInvoices.size === 0) return;

    try {
      const response = await fetch('/api/invoices/bulk-payment-status', {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          invoiceIds: Array.from(selectedInvoices),
          status
        })
      });

      if (response.ok) {
        const result = await response.json();
        toast({
          title: "Estado actualizado",
          description: `Se actualizÃ³ el estado de pago de ${result.updated} factura${result.updated > 1 ? 's' : ''}`,
        });

        // Clear selection after successful update
        setSelectedInvoices(new Set());
      } else {
        throw new Error('Failed to update payment status');
      }
    } catch (error) {
      console.error('Error updating payment status:', error);
      toast({
        title: "Error",
        description: "No se pudo actualizar el estado de pago",
        variant: "destructive",
      });
    }
  };

  // Calculate totals for selected invoices
  const selectedTotals = invoices
    .filter(inv => selectedInvoices.has(inv.id))
    .reduce<{ total: number; iva: number; count: number }>(
      (acc, inv) => ({
        total: acc.total + parseFloat(inv.totalAmount),
        iva: acc.iva + parseFloat(inv.ivaAmount),
        count: acc.count + 1
      }),
      { total: 0, iva: 0, count: 0 }
    );

  const totalPages = Math.ceil(total / pageSize);
  const startIndex = (currentPage - 1) * pageSize + 1;
  const endIndex = Math.min(currentPage * pageSize, total);

  const handleSort = (field: string) => {
    if (!onSortChange) return;
    if (sortBy === field) {
      onSortChange(field, sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      onSortChange(field, 'desc');
    }
  };

  const getSortIcon = (field: string) => {
    if (sortBy !== field) return <ArrowUpDown className="w-3 h-3 ml-1 opacity-50" />;
    return sortOrder === 'asc' 
      ? <ArrowUp className="w-3 h-3 ml-1" /> 
      : <ArrowDown className="w-3 h-3 ml-1" />;
  };

  const handleShareWhatsApp = (invoice: InvoiceWithRelations) => {
    const clientName = invoice.clientProviderName;
    const amount = formatCurrencyWithConfig(invoice.totalAmount, user || undefined);
    const date = formatDate(invoice.date);
    const invoiceNumber = invoice.invoiceNumber || 'sin nÃºmero';
    const type = invoice.type === 'income' ? 'Factura emitida' : 'Factura recibida';

    const message = encodeURIComponent(
      `${type}\n` +
      `Cliente/Proveedor: ${clientName}\n` +
      `NÃºmero: ${invoiceNumber}\n` +
      `Fecha: ${date}\n` +
      `Monto: ${amount}\n` +
      `IVA: ${formatCurrencyWithDecimals(invoice.ivaAmount)}`
    );

    window.open(`https://wa.me/?text=${message}`, '_blank');
  };

  const handleShareEmail = (invoice: InvoiceWithRelations) => {
    const clientName = invoice.clientProviderName;
    const amount = formatCurrencyWithConfig(invoice.totalAmount, user || undefined);
    const date = formatDate(invoice.date);
    const invoiceNumber = invoice.invoiceNumber || 'sin nÃºmero';
    const type = invoice.type === 'income' ? 'Factura emitida' : 'Factura recibida';

    const subject = encodeURIComponent(`${type} - ${clientName} - ${invoiceNumber}`);
    const body = encodeURIComponent(
      `Detalles de la factura:\n\n` +
      `Tipo: ${type}\n` +
      `Cliente/Proveedor: ${clientName}\n` +
      `NÃºmero: ${invoiceNumber}\n` +
      `Fecha: ${date}\n` +
      `Subtotal: ${formatCurrency(invoice.subtotal)}\n` +
      `IVA: ${formatCurrencyWithDecimals(invoice.ivaAmount)}\n` +
      `Total: ${amount}\n\n` +
      `Saludos cordiales,\nOpen Doors`
    );

    window.open(`mailto:?subject=${subject}&body=${body}`, '_blank');
  };

