                 topProviders.map(p => `â€¢ ${p.name} ${p.cuit ? `(CUIT: ${p.cuit})` : ''}`).join('\n');
        }

        // Handle IVA queries
        if (lowerMessage.includes('iva') || lowerMessage.includes('impuesto')) {
          const allInvoices = await storage.getAllInvoices({ limit: 1000 });
