import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import {
  Table,
  TableBody,
  TableCaption,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { User, Upload, Lock, Users, Edit, Trash2, Shield, Camera, Save, AlertCircle, Plus, UserPlus, Settings } from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { z } from "zod";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { userConfigSchema, type UserConfig } from "@shared/schema";

const profileFormSchema = z.object({
  name: z.string().min(2, "El nombre debe tener al menos 2 caracteres"),
  email: z.string().email("Email invÃ¡lido"),
});

const passwordFormSchema = z.object({
  currentPassword: z.string().min(6, "La contraseÃ±a debe tener al menos 6 caracteres"),
  newPassword: z.string().min(6, "La contraseÃ±a debe tener al menos 6 caracteres"),
  confirmPassword: z.string().min(6, "La contraseÃ±a debe tener al menos 6 caracteres"),
}).refine((data) => data.newPassword === data.confirmPassword, {
  message: "Las contraseÃ±as no coinciden",
  path: ["confirmPassword"],
});

const userFormSchema = z.object({
  username: z.string().min(3, "El nombre de usuario debe tener al menos 3 caracteres"),
  displayName: z.string().min(2, "El nombre debe tener al menos 2 caracteres"),
  email: z.string().email("Email invÃ¡lido"),
  password: z.string().min(6, "La contraseÃ±a debe tener al menos 6 caracteres").optional(),
  role: z.enum(["admin", "editor", "viewer"]),
});

type ProfileFormValues = z.infer<typeof profileFormSchema>;
type PasswordFormValues = z.infer<typeof passwordFormSchema>;
type UserFormValues = z.infer<typeof userFormSchema>;
type ConfigFormValues = z.infer<typeof userConfigSchema>;

export default function ProfileSettings() {
  const { user } = useAuth();
  const { toast } = useToast();
  const fileInputRef = useRef<HTMLInputElement>(null);
  const [avatarPreview, setAvatarPreview] = useState<string | null>(null);
  const [editingUser, setEditingUser] = useState<any>(null);
  const [deletingUser, setDeletingUser] = useState<any>(null);
  const [creatingUser, setCreatingUser] = useState(false);
  const [logoPreview, setLogoPreview] = useState<string | null>(null);

  // Fetch all users (admin only)
  const { data: users, isLoading: usersLoading } = useQuery({
    queryKey: ["/api/users"],
    enabled: user?.role === "admin",
  });

  // Profile form
  const profileForm = useForm<ProfileFormValues>({
    resolver: zodResolver(profileFormSchema),
    defaultValues: {
      name: user?.displayName || "",
      email: user?.email || "",
    },
  });

  // Password form
  const passwordForm = useForm<PasswordFormValues>({
    resolver: zodResolver(passwordFormSchema),
    defaultValues: {
      currentPassword: "",
      newPassword: "",
      confirmPassword: "",
    },
  });

  // User form (for creating/editing)
  const userForm = useForm<UserFormValues>({
    resolver: zodResolver(userFormSchema),
    defaultValues: {
      username: "",
      displayName: "",
      email: "",
      password: "",
      role: "viewer",
    },
  });

  // Configuration form
  const configForm = useForm<ConfigFormValues>({
    resolver: zodResolver(userConfigSchema),
    defaultValues: {
      decimalSeparator: (user as any)?.decimalSeparator || ',',
      thousandSeparator: (user as any)?.thousandSeparator || '.',
      decimalPlaces: (user as any)?.decimalPlaces || 2,
      currencySymbol: (user as any)?.currencySymbol || '$',
      currencyPosition: (user as any)?.currencyPosition || 'before',
      roundingMode: (user as any)?.roundingMode || 'round',
      fiscalPeriod: (user as any)?.fiscalPeriod || 'calendar',
    },
  });

  // Update profile mutation
  const updateProfileMutation = useMutation({
    mutationFn: (data: ProfileFormValues) => 
      apiRequest('PUT', '/api/auth/profile', data),
    onSuccess: () => {
      toast({
        title: "Ã‰xito",
        description: "Perfil actualizado correctamente",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Error al actualizar el perfil",
        variant: "destructive",
      });
    },
  });

  // Update password mutation
  const updatePasswordMutation = useMutation({
    mutationFn: (data: PasswordFormValues) => 
      apiRequest('POST', '/api/auth/change-password', data),
    onSuccess: () => {
      toast({
        title: "Ã‰xito",
        description: "ContraseÃ±a actualizada correctamente",
      });
      passwordForm.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Error al actualizar la contraseÃ±a",
        variant: "destructive",
      });
    },
  });

  // Update logo mutation
  const updateLogoMutation = useMutation({
    mutationFn: async (file: File | null) => {
      if (file) {
        const formData = new FormData();
        formData.append("logo", file);

        const res = await fetch('/api/auth/company-logo', {
          method: 'POST',
          body: formData,
          credentials: 'include',
        });

        if (!res.ok) {
          const error = await res.text();
          throw new Error(error || res.statusText);
        }

        return res.json();
      } else {
        // Remove logo
        const res = await fetch('/api/auth/company-logo', {
          method: 'DELETE',
          credentials: 'include',
        });

        if (!res.ok) {
          const error = await res.text();
          throw new Error(error || res.statusText);
        }

        return res.json();
      }
    },
    onSuccess: () => {
      toast({
        title: "Ã‰xito",
        description: "Logo actualizado correctamente",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      setLogoPreview(null);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Error al actualizar el logo",
        variant: "destructive",
      });
    },
  });

  // Update avatar mutation
  const updateAvatarMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append("avatar", file);

      const res = await fetch('/api/auth/avatar', {
        method: 'POST',
        body: formData,
        credentials: 'include',
      });

      if (!res.ok) {
        const error = await res.text();
        throw new Error(error || res.statusText);
      }

      return res.json();
    },
    onSuccess: () => {
      toast({
        title: "Ã‰xito",
        description: "Avatar actualizado correctamente",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      setAvatarPreview(null);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Error al actualizar el avatar",
        variant: "destructive",
      });
    },
  });

  // Update configuration mutation
  const updateConfigMutation = useMutation({
    mutationFn: (data: ConfigFormValues) => 
      apiRequest('PUT', '/api/auth/configuration', data),
    onSuccess: () => {
      toast({
        title: "Ã‰xito",
        description: "ConfiguraciÃ³n actualizada correctamente",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Error al actualizar la configuraciÃ³n",
        variant: "destructive",
      });
    },
  });

  // Create user mutation
  const createUserMutation = useMutation({
    mutationFn: (data: UserFormValues) => 
      apiRequest('POST', '/api/users', data),
    onSuccess: () => {
      toast({
        title: "Ã‰xito",
        description: "Usuario creado correctamente",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      setCreatingUser(false);
      userForm.reset();
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Error al crear el usuario",
        variant: "destructive",
      });
    },
  });

  // Update user mutation
  const updateUserMutation = useMutation({
    mutationFn: ({ id, ...data }: { id: string } & Partial<UserFormValues>) => 
      apiRequest('PUT', `/api/users/${id}`, data),
    onSuccess: () => {
