        title: "Ã‰xito",
        description: "Usuario eliminado correctamente",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });
      setDeletingUser(null);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message || "Error al eliminar el usuario",
        variant: "destructive",
      });
    },
  });

  const handleAvatarChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      // Validate file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        toast({
          title: "Error",
          description: "El archivo no puede superar los 5MB",
          variant: "destructive",
        });
        return;
      }

      // Validate file type
      if (!file.type.startsWith("image/")) {
        toast({
          title: "Error",
          description: "Solo se permiten archivos de imagen",
          variant: "destructive",
        });
        return;
      }

      // Create preview
      const reader = new FileReader();
      reader.onloadend = () => {
        setAvatarPreview(reader.result as string);
      };
      reader.readAsDataURL(file);

      // Upload file
      updateAvatarMutation.mutate(file);
    }
  };

  const onProfileSubmit = (data: ProfileFormValues) => {
    updateProfileMutation.mutate(data);
  };

  const onPasswordSubmit = (data: PasswordFormValues) => {
    updatePasswordMutation.mutate(data);
  };

  const handleLogoUpload = (file: File) => {
    if (file.size > 5 * 1024 * 1024) {
      toast({
        title: "Error",
        description: "El archivo debe ser menor a 5MB",
        variant: "destructive",
      });
      return;
    }

    const reader = new FileReader();
    reader.onloadend = () => {
      setLogoPreview(reader.result as string);
    };
    reader.readAsDataURL(file);

    updateLogoMutation.mutate(file);
  };

  const handleRemoveLogo = () => {
    updateLogoMutation.mutate(null);
  };

  const onUserSubmit = (data: UserFormValues) => {
    if (editingUser) {
      updateUserMutation.mutate({ id: editingUser.id, ...data });
    } else {
      createUserMutation.mutate(data);
    }
  };

  const getRoleBadgeColor = (role: string) => {
    switch (role) {
      case "admin":
        return "destructive";
      case "editor":
        return "secondary";
      case "viewer":
        return "default";
      default:
        return "default";
    }
  };

  const getUserInitials = (name: string) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase()
      .slice(0, 2);
  };

  if (!user) {
    return (
      <div className="p-8">
        <Alert>
          <AlertCircle className="h-4 w-4" />
          <AlertDescription>
            No se pudo cargar la informaciÃ³n del usuario
          </AlertDescription>
        </Alert>
      </div>
    );
  }

  return (
    <div className="p-8 space-y-8">
      <div>
        <h2 className="text-2xl font-bold tracking-tight">ConfiguraciÃ³n de Perfil</h2>
        <p className="text-muted-foreground">
          Administra tu informaciÃ³n personal y configuraciÃ³n de seguridad
        </p>
      </div>

      <Tabs defaultValue="profile" className="space-y-4">
        <TabsList>
          <TabsTrigger value="profile" data-testid="tab-profile">
            <User className="w-4 h-4 mr-2" />
            Perfil
          </TabsTrigger>
          <TabsTrigger value="security" data-testid="tab-security">
            <Lock className="w-4 h-4 mr-2" />
            Seguridad
          </TabsTrigger>
          <TabsTrigger value="configuration" data-testid="tab-configuration">
            <Settings className="w-4 h-4 mr-2" />
            ConfiguraciÃ³n
          </TabsTrigger>
          {user.role === "admin" && (
            <TabsTrigger value="users" data-testid="tab-users">
              <Users className="w-4 h-4 mr-2" />
              Usuarios
            </TabsTrigger>
          )}
        </TabsList>

        <TabsContent value="profile" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>InformaciÃ³n Personal</CardTitle>
              <CardDescription>
                Actualiza tu informaciÃ³n de perfil y avatar
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* Avatar Section */}
              <div className="flex items-center space-x-4">
                <Avatar className="h-24 w-24">
                  <AvatarImage
                    src={avatarPreview || user.avatar || undefined}
                    alt={user.displayName}
                  />
                  <AvatarFallback>{getUserInitials(user.displayName || "User")}</AvatarFallback>
                </Avatar>
                <div className="space-y-2">
                  <Button
                    variant="outline"
                    onClick={() => fileInputRef.current?.click()}
                    disabled={updateAvatarMutation.isPending}
                    data-testid="upload-avatar-button"
                  >
                    <Camera className="w-4 h-4 mr-2" />
                    Cambiar Avatar
                  </Button>
                  <input
                    ref={fileInputRef}
                    type="file"
                    accept="image/*"
                    onChange={handleAvatarChange}
                    className="hidden"
                    data-testid="avatar-file-input"
                  />
                  <p className="text-xs text-muted-foreground">
                    JPG, PNG o GIF. Max 5MB.
                  </p>
                </div>
              </div>

              {/* Profile Form */}
              <Form {...profileForm}>
                <form onSubmit={profileForm.handleSubmit(onProfileSubmit)} className="space-y-4">
                  <FormField
                    control={profileForm.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Nombre</FormLabel>
                        <FormControl>
                          <Input {...field} data-testid="input-name" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={profileForm.control}
                    name="email"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Email</FormLabel>
                        <FormControl>
                          <Input {...field} type="email" data-testid="input-email" />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <div className="flex items-center space-x-2">
                    <Label>Rol:</Label>
                    <Badge variant={getRoleBadgeColor(user.role)}>
                      {user.role === "admin" ? (
                        <>
                          <Shield className="w-3 h-3 mr-1" />
                          Administrador
                        </>
                      ) : user.role === "editor" ? (
                        "Editor"
                      ) : (
                        "Visor"
                      )}
                    </Badge>
                  </div>
                  <Button
                    type="submit"
                    disabled={updateProfileMutation.isPending}
                    data-testid="save-profile-button"
                  >
                    <Save className="w-4 h-4 mr-2" />
                    Guardar Cambios
                  </Button>
                </form>
              </Form>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="security" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Cambiar ContraseÃ±a</CardTitle>
              <CardDescription>
                Actualiza tu contraseÃ±a de acceso al sistema
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Form {...passwordForm}>
                <form onSubmit={passwordForm.handleSubmit(onPasswordSubmit)} className="space-y-4">
                  <FormField
                    control={passwordForm.control}
                    name="currentPassword"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>ContraseÃ±a Actual</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            type="password"
                            data-testid="input-current-password"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={passwordForm.control}
                    name="newPassword"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Nueva ContraseÃ±a</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            type="password"
                            data-testid="input-new-password"
                          />
                        </FormControl>
                        <FormDescription>
                          MÃ­nimo 6 caracteres
                        </FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={passwordForm.control}
                    name="confirmPassword"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Confirmar Nueva ContraseÃ±a</FormLabel>
                        <FormControl>
                          <Input
                            {...field}
                            type="password"
                            data-testid="input-confirm-password"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <Button
                    type="submit"
                    disabled={updatePasswordMutation.isPending}
                    data-testid="change-password-button"
                  >
                    <Lock className="w-4 h-4 mr-2" />
                    Actualizar ContraseÃ±a
                  </Button>
                </form>
              </Form>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="configuration" className="space-y-6">
          {/* Company Logo Section - Admin Only */}
          {user.role === "admin" && (
            <Card>
              <CardHeader>
                <CardTitle>Logo de la Empresa</CardTitle>
                <CardDescription>
                  Personaliza tu empresa con un logo que aparecerÃ¡ en reportes y documentos
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div className="flex items-center space-x-4">
                  {(user as any)?.companyLogo ? (
                    <img 
                      src={(user as any).companyLogo} 
                      alt="Logo de la empresa" 
                      className="h-24 w-auto rounded border"
                    />
                  ) : (
                    <div className="h-24 w-24 rounded border-2 border-dashed border-gray-300 flex items-center justify-center">
                      <Upload className="h-8 w-8 text-gray-400" />
                    </div>
                  )}
                  <div className="space-y-2">
                    <Button
                      variant="outline"
                      onClick={() => document.getElementById('logo-input')?.click()}
                      disabled={updateLogoMutation?.isPending}
                      data-testid="upload-logo-button"
                    >
                      <Upload className="w-4 h-4 mr-2" />
                      {(user as any)?.companyLogo ? 'Cambiar Logo' : 'Subir Logo'}
                    </Button>
                    <input
                      id="logo-input"
                      type="file"
                      accept="image/*"
                      onChange={(e) => {
                        const file = e.target.files?.[0];
                        if (file) {
                          handleLogoUpload(file);
                        }
                      }}
                      className="hidden"
                      data-testid="logo-file-input"
                    />
                    <p className="text-xs text-muted-foreground">
                      PNG, JPG o SVG. Max 5MB. Recomendado: 200x200px
                    </p>
                    {(user as any)?.companyLogo && (
                      <Button
                        variant="destructive"
                        size="sm"
                        onClick={handleRemoveLogo}
                        data-testid="remove-logo-button"
                      >
                        <Trash2 className="w-3 h-3 mr-1" />
                        Eliminar Logo
                      </Button>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          )}

          <Card>
            <CardHeader>
              <CardTitle>ConfiguraciÃ³n NumÃ©rica</CardTitle>
              <CardDescription>
                Ajusta el formato de nÃºmeros, decimales y moneda segÃºn tus preferencias
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Form {...configForm}>
                <form onSubmit={configForm.handleSubmit((data) => updateConfigMutation.mutate(data))} className="space-y-6">
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="space-y-4">
                      <FormField
                        control={configForm.control}
                        name="decimalSeparator"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Separador Decimal</FormLabel>
                            <Select onValueChange={field.onChange} defaultValue={field.value}>
                              <FormControl>
                                <SelectTrigger data-testid="select-decimal-separator">
                                  <SelectValue placeholder="Seleccionar separador" />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <SelectItem value=",">, (coma) - Formato argentino</SelectItem>
                                <SelectItem value=".">. (punto) - Formato internacional</SelectItem>
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={configForm.control}
                        name="thousandSeparator"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Separador de Miles</FormLabel>
                            <Select onValueChange={field.onChange} defaultValue={field.value}>
                              <FormControl>
                                <SelectTrigger data-testid="select-thousand-separator">
                                  <SelectValue placeholder="Seleccionar separador" />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <SelectItem value=".">. (punto) - Formato argentino</SelectItem>
                                <SelectItem value=",">, (coma) - Formato internacional</SelectItem>
                                <SelectItem value=" "> (espacio)</SelectItem>
                                <SelectItem value="none">Sin separador</SelectItem>
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={configForm.control}
                        name="decimalPlaces"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Decimales</FormLabel>
                            <Select onValueChange={(value) => field.onChange(Number(value))} defaultValue={field.value?.toString()}>
                              <FormControl>
                                <SelectTrigger data-testid="select-decimal-places">
                                  <SelectValue placeholder="NÃºmero de decimales" />
                                </SelectTrigger>
                              </FormControl>
                              <SelectContent>
                                <SelectItem value="0">0 decimales</SelectItem>
                                <SelectItem value="2">2 decimales (recomendado)</SelectItem>
                                <SelectItem value="3">3 decimales</SelectItem>
                                <SelectItem value="4">4 decimales</SelectItem>
                              </SelectContent>
                            </Select>
                            <FormMessage />
                          </FormItem>
                        )}
                      />

                      <FormField
                        control={configForm.control}
                        name="fiscalPeriod"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>PerÃ­odo Fiscal</FormLabel>
                            <Select onValueChange={field.onChange} defaultValue={field.value}>
                              <FormControl>
                                <SelectTrigger data-testid="select-fiscal-period">
                                  <SelectValue placeholder="Seleccionar perÃ­odo" />
                                </SelectTrigger>
