import { useState, useEffect } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { apiRequest } from "@/lib/queryClient";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Plus, X, Calculator, Info } from "lucide-react";
import { formatCurrencyWithDecimals, formatCurrencyWithConfig } from "@/lib/utils";
import type { IvaComponent } from "@shared/schema";

interface IvaComponentsProps {
  invoiceId: string;
  totalAmount: number;
  currentIvaAmount: number;
  editable?: boolean;
}

export function IvaComponents({
  invoiceId,
  totalAmount,
  currentIvaAmount,
  editable = true,
}: IvaComponentsProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [components, setComponents] = useState<
    Array<{ description: string; percentage: number; amount: number }>
  >([]);
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const { user } = useAuth();

  // Check if user has permission to edit
  const canEdit = editable && user && (user.role === 'admin' || user.role === 'editor');

  // Fetch existing IVA components - always enabled for read-only indicator
  const { data: existingComponents, isLoading } = useQuery<IvaComponent[]>({
    queryKey: [`/api/invoices/${invoiceId}/iva-components`],
    enabled: true, // Always fetch to show indicator
  });

  // Update components when data is loaded
  useEffect(() => {
    if (isOpen && existingComponents) {
      if (existingComponents.length > 0) {
        setComponents(
          existingComponents.map((c) => ({
            description: c.description,
            percentage: Number(c.percentage),
            amount: Number(c.amount),
          }))
        );
      } else {
        // Default to a single component with current IVA
        setComponents([
          {
            description: "IVA 21%",
            percentage: 21,
            amount: currentIvaAmount,
          },
        ]);
      }
    }
  }, [isOpen, existingComponents, currentIvaAmount]);

  // When dialog opens
  const handleDialogOpen = () => {
    setIsOpen(true);
  };

  // Add new component
  const addComponent = () => {
    setComponents([
      ...components,
      {
        description: "",
        percentage: 0,
        amount: 0,
      },
    ]);
  };

  // Remove component
  const removeComponent = (index: number) => {
    setComponents(components.filter((_, i) => i !== index));
  };

  // Update component
  const updateComponent = (
    index: number,
    field: keyof (typeof components)[0],
    value: string | number
  ) => {
    const updated = [...components];
    if (field === "percentage" || field === "amount") {
      const numValue = parseFloat(value as string) || 0;
      updated[index][field] = numValue;

      // Auto-calculate amount from percentage or percentage from amount
      if (field === "percentage") {
        updated[index].amount = (totalAmount * numValue) / 100;
      } else if (field === "amount" && totalAmount > 0) {
        updated[index].percentage = (numValue * 100) / totalAmount;
      }
    } else {
      updated[index][field] = value as string;
    }
    setComponents(updated);
  };

  // Save IVA components
  const saveMutation = useMutation({
    mutationFn: async () => {
      // First delete existing components
      await apiRequest(`/api/invoices/${invoiceId}/iva-components`, "DELETE");

      // Then create new ones
      for (const component of components) {
        if (component.amount > 0) {
          await apiRequest(`/api/invoices/${invoiceId}/iva-components`, "POST", {
            description: component.description || `IVA ${component.percentage}%`,
            percentage: component.percentage,
            amount: component.amount,
          });
        }
      }
    },
    onSuccess: () => {
      toast({
        title: "Componentes IVA actualizados",
        description: "Los componentes de IVA se guardaron correctamente",
      });
      queryClient.invalidateQueries({ queryKey: [`/api/invoices/${invoiceId}/iva-components`] });
      queryClient.invalidateQueries({ queryKey: ['/api/invoices'] });
      setIsOpen(false);
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: "No se pudieron guardar los componentes de IVA",
        variant: "destructive",
      });
      console.error("Error saving IVA components:", error);
    },
  });

  const totalIva = components.reduce((sum, c) => sum + c.amount, 0);
  const difference = Math.abs(totalIva - currentIvaAmount);
  const isValid = totalIva > 0 && 
                 components.every(c => c.description) && 
                 difference < 0.01; // Allow 1 cent tolerance

  if (!canEdit) {
    return (
      <div className="flex items-center gap-2" data-testid="iva-amount-display">
        <span className="text-sm">{formatCurrencyWithConfig(currentIvaAmount, user || undefined)}</span>
        {existingComponents && existingComponents.length > 1 && (
          <span title="MÃºltiples componentes IVA">
            <Info className="w-4 h-4 text-muted-foreground" />
          </span>
        )}
      </div>
    );
  }

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button
          variant="ghost"
          size="sm"
          onClick={handleDialogOpen}
          className="text-sm hover:bg-accent/50"
          data-testid="iva-components-button"
        >
          <Calculator className="w-4 h-4 mr-1" />
          {formatCurrencyWithConfig(currentIvaAmount, user || undefined)}
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-2xl mx-4 sm:mx-0">
        <DialogHeader>
          <DialogTitle>Componentes de IVA Gradual</DialogTitle>
          <DialogDescription>
            Configure los diferentes componentes de IVA para esta factura. Puede agregar mÃºltiples alÃ­cuotas o pagos parciales.
          </DialogDescription>
        </DialogHeader>

        {isLoading ? (
          <div>Cargando componentes...</div>
        ) : (
          <div className="space-y-4">
            <div className="bg-muted/50 p-4 rounded-lg">
              <div className="grid grid-cols-3 gap-4 mb-2">
                <div className="text-sm text-muted-foreground">Total Factura:</div>
                <div className="text-sm font-medium">{formatCurrencyWithConfig(totalAmount, user || undefined)}</div>
                <div></div>
              </div>
              <div className="grid grid-cols-3 gap-4">
                <div className="text-sm text-muted-foreground">IVA Total Actual:</div>
                <div className="text-sm font-medium">{formatCurrencyWithConfig(currentIvaAmount, user || undefined)}</div>
                <div></div>
              </div>
            </div>

            <div className="space-y-3">
              {components.map((component, index) => (
                <div
                  key={index}
                  className="grid grid-cols-12 gap-2 items-end p-3 border rounded-lg"
                  data-testid={`iva-component-${index}`}
                >
                  <div className="col-span-5">
                    <Label htmlFor={`desc-${index}`}>DescripciÃ³n</Label>
                    <Input
                      id={`desc-${index}`}
                      placeholder="Ej: IVA 21%, IVA 10.5%, PercepciÃ³n"
                      value={component.description}
                      onChange={(e) => updateComponent(index, "description", e.target.value)}
                      data-testid={`iva-description-${index}`}
                    />
                  </div>
                  <div className="col-span-3">
                    <Label htmlFor={`perc-${index}`}>Porcentaje</Label>
                    <Input
                      id={`perc-${index}`}
                      type="number"
                      step="0.01"
                      placeholder="0.00"
                      value={component.percentage}
                      onChange={(e) => updateComponent(index, "percentage", e.target.value)}
                      data-testid={`iva-percentage-${index}`}
                    />
                  </div>
                  <div className="col-span-3">
                    <Label htmlFor={`amount-${index}`}>Monto</Label>
                    <Input
                      id={`amount-${index}`}
                      type="number"
                      step="0.01"
                      placeholder="0.00"
                      value={component.amount}
                      onChange={(e) => updateComponent(index, "amount", e.target.value)}
                      data-testid={`iva-amount-${index}`}
                    />
                  </div>
                  <div className="col-span-1">
                    <Button
                      variant="ghost"
                      size="icon"
                      onClick={() => removeComponent(index)}
                      disabled={components.length === 1}
                      data-testid={`remove-component-${index}`}
                    >
                      <X className="w-4 h-4" />
                    </Button>
                  </div>
                </div>
              ))}
            </div>

            <Button
              variant="outline"
              size="sm"
              onClick={addComponent}
              className="w-full"
              data-testid="add-component"
            >
              <Plus className="w-4 h-4 mr-2" />
              Agregar Componente
            </Button>

            <div className="bg-primary/10 p-4 rounded-lg">
              <div className="flex justify-between items-center">
                <span className="font-medium">Total IVA Configurado:</span>
                <span className={`font-bold text-lg ${
                  difference < 0.01 
                    ? 'text-green-600 dark:text-green-400' 
                    : 'text-red-600 dark:text-red-400'
                }`}>
                  {formatCurrencyWithConfig(totalIva, user || undefined)}
                </span>
              </div>
              {difference >= 0.01 && (
                <div className="space-y-2 mt-2">
                  <div className="text-sm text-red-600 dark:text-red-400">
                    âš ï¸ El total debe coincidir con el IVA de la factura
                  </div>
                  <div className="text-sm text-muted-foreground">
                    Diferencia: {formatCurrencyWithConfig(difference, user || undefined)} 
                    (IVA actual: {formatCurrencyWithConfig(currentIvaAmount, user || undefined)})
                  </div>
                </div>
              )}
            </div>

            <div className="flex justify-end gap-2 pt-4 border-t">
              <Button
                variant="outline"
                onClick={() => setIsOpen(false)}
                data-testid="cancel-button"
              >
                Cancelar
              </Button>
              <Button
                onClick={() => saveMutation.mutate()}
                disabled={!isValid || saveMutation.isPending}
                data-testid="save-components"
              >
                {saveMutation.isPending ? "Guardando..." : "Guardar Componentes"}
              </Button>
            </div>
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ExcelImportExport..content:
import { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import {
  Upload,
  Download,
  FileSpreadsheet,
  AlertCircle,
  CheckCircle,
  X,
} from "lucide-react";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Progress } from "@/components/ui/progress";

export function ExcelImportExport() {
  const [isOpen, setIsOpen] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [importProgress, setImportProgress] = useState(0);
  const [importResults, setImportResults] = useState<{
    success: number;
    failed: number;
    errors: string[];
  } | null>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Export mutation
  const exportMutation = useMutation({
    mutationFn: async (options: {
      startMonth?: number;
      startYear?: number;
      endMonth?: number;
      endYear?: number;
      type?: 'income' | 'expense';
    }) => {
      const params = new URLSearchParams();
      if (options.startMonth) params.append('startMonth', options.startMonth.toString());
      if (options.startYear) params.append('startYear', options.startYear.toString());
      if (options.endMonth) params.append('endMonth', options.endMonth.toString());
      if (options.endYear) params.append('endYear', options.endYear.toString());
      if (options.type) params.append('type', options.type);

      const response = await fetch(`/api/export/excel?${params}`, {
        method: 'GET',
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error('Error al exportar datos');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `facturas_${new Date().toISOString().split('T')[0]}.xlsx`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    },
    onSuccess: () => {
      toast({
        title: "ExportaciÃ³n exitosa",
        description: "El archivo Excel se ha descargado correctamente",
      });
    },
    onError: (error) => {
      toast({
        title: "Error en exportaciÃ³n",
        description: error instanceof Error ? error.message : "Error desconocido",
        variant: "destructive",
      });
    },
  });

  // Import mutation
  const importMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append('file', file);

      setImportProgress(50); // Set to 50% while uploading

      const response = await fetch('/api/import/excel', {
        method: 'POST',
        body: formData,
        credentials: 'include',
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Error al importar datos');
      }

      return response.json();
    },
    onSuccess: (data) => {
      setImportResults(data);
      setImportProgress(100);

      // Invalidate all related queries
      queryClient.invalidateQueries({ queryKey: ['/api/invoices'] });
      queryClient.invalidateQueries({ queryKey: ['/api/clients-providers'] });
      queryClient.invalidateQueries({ queryKey: ['/api/kpis'] });
      queryClient.invalidateQueries({ queryKey: ['/api/reports'] });

      toast({
        title: "ImportaciÃ³n completada",
        description: `${data.success} facturas importadas, ${data.failed} fallidas`,
      });
    },
    onError: (error) => {
      toast({
        title: "Error en importaciÃ³n",
        description: error instanceof Error ? error.message : "Error desconocido",
        variant: "destructive",
      });
      setImportProgress(0);
    },
  });

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      if (!file.name.endsWith('.xlsx') && !file.name.endsWith('.xls')) {
        toast({
          title: "Archivo invÃ¡lido",
          description: "Por favor seleccione un archivo Excel (.xlsx o .xls)",
          variant: "destructive",
        });
        return;
      }
      setSelectedFile(file);
      setImportResults(null);
      setImportProgress(0);
    }
  };

  const handleImport = () => {
    if (selectedFile) {
      importMutation.mutate(selectedFile);
    }
  };

  const handleExportAll = () => {
    exportMutation.mutate({});
  };

  const handleExportFiscalPeriod = () => {
    const currentYear = new Date().getFullYear();
    const currentMonth = new Date().getMonth() + 1;

    // If we're past May, use current fiscal year. Otherwise use previous
    const startYear = currentMonth >= 5 ? currentYear : currentYear - 1;

    exportMutation.mutate({
      startMonth: 5,
      startYear,
      endMonth: 4,
      endYear: startYear + 1,
    });
  };

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>
        <Button variant="success" data-testid="excel-import-export-button">
          <FileSpreadsheet className="w-4 h-4 mr-2" />
          Excel
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-2xl mx-4 sm:mx-0">
        <DialogHeader>
          <DialogTitle>Importar/Exportar Excel</DialogTitle>
          <DialogDescription>
            Importe facturas desde Excel o exporte sus datos en formato Excel
          </DialogDescription>
        </DialogHeader>

        <Tabs defaultValue="export" className="w-full">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="export">Exportar</TabsTrigger>
            <TabsTrigger value="import">Importar</TabsTrigger>
          </TabsList>

          <TabsContent value="export" className="space-y-4">
            <div className="space-y-4">
              <Alert>
                <AlertCircle className="h-4 w-4" />
                <AlertDescription>
                  El archivo Excel incluirÃ¡ todas las facturas con los siguientes campos:
                  Fecha, Tipo, Emisor, Cliente/Proveedor, CUIT, NÃºmero, Subtotal, IVA, Total,
                  Clase (A/B/C), IIBB, Ganancias, Otros Impuestos, Estado de Pago
                </AlertDescription>
              </Alert>

              <div className="grid gap-4">
                <Button
                  onClick={handleExportAll}
                  disabled={exportMutation.isPending}
                  variant="success"
                  className="w-full"
                  data-testid="export-all-button"
                >
                  <Download className="w-4 h-4 mr-2" />
                  {exportMutation.isPending ? "Exportando..." : "Exportar Todas las Facturas"}
                </Button>

                <Button
                  onClick={handleExportFiscalPeriod}
                  disabled={exportMutation.isPending}
                  variant="success"
                  className="w-full"
                  data-testid="export-fiscal-button"
                >
                  <Download className="w-4 h-4 mr-2" />
                  {exportMutation.isPending ? "Exportando..." : "Exportar PerÃ­odo Fiscal Actual"}
                </Button>
              </div>
            </div>
          </TabsContent>

          <TabsContent value="import" className="space-y-4">
            <Alert>
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>
                El archivo Excel debe contener las siguientes columnas en orden:
                <div className="mt-2 text-xs font-mono">
                  Fecha | Tipo | Emisor | Cliente | CUIT | NÃºmero | Subtotal | IVA | Total | 
                  Clase | IIBB | Ganancias | Otros
                </div>
              </AlertDescription>
            </Alert>

            <div className="space-y-4">
              <div>
                <Label htmlFor="excel-file">Seleccionar archivo Excel</Label>
                <Input
                  id="excel-file"
                  type="file"
                  accept=".xlsx,.xls"
                  onChange={handleFileSelect}
                  disabled={importMutation.isPending}
                  data-testid="excel-file-input"
                />
              </div>

              {selectedFile && (
                <div className="flex items-center justify-between p-3 border rounded-lg bg-muted/50">
                  <div className="flex items-center gap-2">
                    <FileSpreadsheet className="w-4 h-4 text-muted-foreground" />
                    <span className="text-sm">{selectedFile.name}</span>
                    <span className="text-xs text-muted-foreground">
                      ({(selectedFile.size / 1024).toFixed(2)} KB)
                    </span>
                  </div>
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={() => {
                      setSelectedFile(null);
                      setImportResults(null);
                      setImportProgress(0);
                    }}
                    disabled={importMutation.isPending}
                  >
                    <X className="w-4 h-4" />
                  </Button>
                </div>
              )}

              {importProgress > 0 && importProgress < 100 && (
                <div className="space-y-2">
                  <Progress value={importProgress} />
                  <p className="text-sm text-muted-foreground text-center">
                    Importando... {importProgress}%
                  </p>
                </div>
              )}

              {importResults && (
                <Alert className={importResults.failed > 0 ? "border-orange-500" : "border-green-500"}>
                  <CheckCircle className="h-4 w-4" />
                  <AlertDescription>
                    <div className="space-y-2">
                      <div>
                        âœ… {importResults.success} facturas importadas exitosamente
                      </div>
                      {importResults.failed > 0 && (
                        <div>
                          âš ï¸ {importResults.failed} facturas no pudieron importarse
                        </div>
                      )}
                      {importResults.errors.length > 0 && (
                        <div className="mt-2 p-2 bg-muted rounded text-xs">
                          <div className="font-semibold mb-1">Errores:</div>
                          {importResults.errors.slice(0, 5).map((error, i) => (
                            <div key={i}>â€¢ {error}</div>
                          ))}
                          {importResults.errors.length > 5 && (
                            <div>... y {importResults.errors.length - 5} mÃ¡s</div>
                          )}
                        </div>
                      )}
                    </div>
                  </AlertDescription>
                </Alert>
              )}

              <Button
                onClick={handleImport}
                disabled={!selectedFile || importMutation.isPending}
                variant="success"
                className="w-full"
                data-testid="import-button"
              >
                <Upload className="w-4 h-4 mr-2" />
                {importMutation.isPending ? "Importando..." : "Importar Facturas"}
              </Button>
            </div>
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

client/src/styles/dialog-overrides.css.content:
/* Dialog and Modal Layout Improvements */

/* Improve dialog content spacing and mobile responsiveness */
[role="dialog"] {
  /* Better spacing from screen edges on mobile */
  margin: 1rem;
  max-height: calc(100vh - 2rem);
  overflow-y: auto;
}

/* Dialog content adjustments for all sizes */
@media (min-width: 640px) {
  [role="dialog"] {
    margin: 2rem;
    max-height: calc(100vh - 4rem);
  }
}

/* Better form field spacing in dialogs */
.dialog-form-section {
  padding: 0.75rem;
  background: hsl(var(--muted) / 0.3);
  border-radius: 0.5rem;
  margin-bottom: 1rem;
}

/* Improved grid layouts for forms */
.form-grid-2 {
  display: grid;
  grid-template-columns: 1fr;
  gap: 1rem;
}

@media (min-width: 640px) {
  .form-grid-2 {
    grid-template-columns: repeat(2, 1fr);
  }
}

.form-grid-3 {
  display: grid;
  grid-template-columns: 1fr;
  gap: 1rem;
}

@media (min-width: 768px) {
  .form-grid-3 {
    grid-template-columns: repeat(3, 1fr);
  }
}

/* Better select dropdown content on mobile */
[role="listbox"] {
  max-height: 40vh;
}

/* Improved dialog footer spacing */
.dialog-footer-custom {
  display: flex;
  flex-direction: column-reverse;
  gap: 0.75rem;
  margin-top: 1.5rem;
  padding-top: 1rem;
  border-top: 1px solid hsl(var(--border));
}

@media (min-width: 640px) {
  .dialog-footer-custom {
    flex-direction: row;
    justify-content: flex-end;
  }
}

/* Better label and input spacing */
.form-field {
  display: flex;
  flex-direction: column;
  gap: 0.375rem;
}

.form-field label {
  font-size: 0.875rem;
  font-weight: 500;
  color: hsl(var(--foreground));
}

/* Improved scrollbar styling for dialog content */
[role="dialog"]::-webkit-scrollbar {
  width: 8px;
}

[role="dialog"]::-webkit-scrollbar-track {
  background: hsl(var(--muted) / 0.1);
  border-radius: 4px;
}

[role="dialog"]::-webkit-scrollbar-thumb {
  background: hsl(var(--muted));
  border-radius: 4px;
}

[role="dialog"]::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--muted) / 0.8);
}

client/src/styles/color-updates.css.content:
/* Color scheme updates - Replace red with blue/green accents */

/* Override destructive colors */
.text-destructive {
  color: hsl(0, 84.2%, 60.2%) !important; /* Red for delete actions */
}

.bg-destructive {
  background-color: hsl(0, 84.2%, 60.2%) !important;
}

.border-destructive {
  border-color: hsl(0, 84.2%, 60.2%) !important;
}

/* Icon color improvements */
.icon-primary {
  color: hsl(221.2, 83.2%, 53.3%);
}

.icon-success {
  color: hsl(142, 70%, 45%);
}

.icon-warning {
  color: hsl(45, 93%, 47%);
}

.icon-info {
  color: hsl(199, 89%, 48%);
}

.icon-muted {
  color: hsl(215.4, 16.3%, 56.9%);
}

/* Status colors */
.status-active {
  color: hsl(142, 70%, 45%);
}

.status-inactive {
  color: hsl(215.4, 16.3%, 56.9%);
}

.status-pending {
  color: hsl(45, 93%, 47%);
}

.status-completed {
  color: hsl(199, 89%, 48%);
}

/* Button icon colors */
.btn-icon-primary {
  color: hsl(221.2, 83.2%, 53.3%);
