}

/* Replace red expense colors with softer alternative */
.expense-color {
  color: hsl(25, 95%, 53%) !important; /* Orange instead of red */
}

.bg-expense {
  background-color: hsl(25, 95%, 53%, 0.1) !important;
}

/* Override specific components */
.lucide-trash-2 {
  color: hsl(0, 84.2%, 60.2%) !important; /* Red for delete icons */
}

.lucide-x-circle {
  color: hsl(45, 93%, 47%) !important; /* Yellow/warning for close/cancel */
