            className="h-[80vh] p-0"
            data-testid="date-range-sheet"
          >
            <SheetHeader className="px-4 pt-4 pb-2">
              <SheetTitle>Seleccionar PerÃ­odo</SheetTitle>
            </SheetHeader>
