            <span className="text-xs sm:text-sm">RevisiÃ³n</span>
          </TabsTrigger>
          <TabsTrigger value="data-management" data-testid="tab-data-management" className="flex flex-col items-center justify-center p-2 sm:flex-row sm:p-3 text-xs sm:text-sm" aria-label="GestiÃ³n">
