                                    name="clientProviderName"
                                    render={({ field }) => (
                                      <FormItem>
                                        <FormLabel>Cliente/Proveedor *</FormLabel>
                                        <FormControl>
                                          <Input {...field} placeholder="Nombre del cliente o proveedor" />
                                        </FormControl>
                                        <FormMessage />
                                      </FormItem>
                                    )}
                                  />

                                  <div className="grid grid-cols-2 gap-4">
                                    <FormField
                                      control={form.control}
                                      name="type"
                                      render={({ field }) => (
                                        <FormItem>
                                          <FormLabel>Tipo *</FormLabel>
                                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                                            <FormControl>
                                              <SelectTrigger>
                                                <SelectValue placeholder="Seleccionar tipo" />
                                              </SelectTrigger>
                                            </FormControl>
                                            <SelectContent>
                                              <SelectItem value="income">Ingreso</SelectItem>
                                              <SelectItem value="expense">Egreso</SelectItem>
                                            </SelectContent>
                                          </Select>
                                          <FormMessage />
                                        </FormItem>
                                      )}
                                    />

                                    <FormField
                                      control={form.control}
                                      name="invoiceClass"
                                      render={({ field }) => (
                                        <FormItem>
                                          <FormLabel>Clase de Factura *</FormLabel>
                                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                                            <FormControl>
                                              <SelectTrigger>
                                                <SelectValue placeholder="Seleccionar clase" />
                                              </SelectTrigger>
                                            </FormControl>
                                            <SelectContent>
                                              <SelectItem value="A">Tipo A - Responsable Inscripto</SelectItem>
                                              <SelectItem value="B">Tipo B - Consumidor Final</SelectItem>
                                              <SelectItem value="C">Tipo C - Monotributista</SelectItem>
                                            </SelectContent>
                                          </Select>
                                          <FormMessage />
                                        </FormItem>
                                      )}
                                    />
                                  </div>

                                  <div className="grid grid-cols-3 gap-4">
                                    <FormField
                                      control={form.control}
                                      name="subtotal"
                                      render={({ field }) => (
                                        <FormItem>
                                          <FormLabel>Subtotal *</FormLabel>
                                          <FormControl>
                                            <Input {...field} type="number" step="0.01" placeholder="0.00" />
                                          </FormControl>
                                          <FormMessage />
                                        </FormItem>
                                      )}
                                    />

                                    <FormField
                                      control={form.control}
                                      name="ivaAmount"
                                      render={({ field }) => (
                                        <FormItem>
                                          <FormLabel>IVA *</FormLabel>
                                          <FormControl>
                                            <Input {...field} type="number" step="0.01" placeholder="0.00" />
                                          </FormControl>
                                          <FormMessage />
                                        </FormItem>
                                      )}
                                    />

                                    <FormField
                                      control={form.control}
                                      name="totalAmount"
                                      render={({ field }) => (
                                        <FormItem>
                                          <FormLabel>Total *</FormLabel>
                                          <FormControl>
                                            <Input {...field} type="number" step="0.01" placeholder="0.00" />
                                          </FormControl>
                                          <FormMessage />
                                        </FormItem>
                                      )}
                                    />
                                  </div>

                                  <div className="flex justify-end gap-2 pt-4">
                                    <Button 
                                      type="button" 
                                      variant="outline" 
                                      onClick={() => setIsEditModalOpen(false)}
                                    >
                                      <X className="w-4 h-4 mr-2" />
                                      Cancelar
                                    </Button>
                                    <Button 
                                      type="submit" 
                                      disabled={updateInvoiceMutation.isPending}
                                      data-testid="save-invoice-button"
                                    >
                                      <Save className="w-4 h-4 mr-2" />
                                      {updateInvoiceMutation.isPending ? 'Guardando...' : 'Guardar Cambios'}
                                    </Button>
                                  </div>
                                </form>
                              </Form>
                            </DialogContent>
                          </Dialog>

                          {/* View File Button */}
                          <Button 
                            variant="outline" 
                            size="sm"
                            onClick={() => handleViewFile(invoice.id)}
                            data-testid={`view-file-${invoice.id}`}
                            title="Ver archivo original"
                          >
                            <Eye className="w-4 h-4" />
                          </Button>

                          {/* Approve Button */}
                          <Button 
                            variant="default" 
                            size="sm"
                            disabled={missingFields.length > 0 || approveInvoiceMutation.isPending}
                            onClick={() => handleApproveInvoice(invoice.id)}
                            data-testid={`approve-invoice-${invoice.id}`}
                            title={missingFields.length > 0 ? 'Complete los datos faltantes' : 'Aprobar factura'}
                          >
                            <CheckCircle className="w-4 h-4" />
                          </Button>

                          {/* Delete Button */}
                          <Button 
                            size="sm"
                            disabled={deleteInvoiceMutation.isPending}
                            onClick={() => handleDeleteInvoice(invoice.id)}
                            data-testid={`delete-invoice-${invoice.id}`}
                            title="Eliminar factura permanentemente"
                            className="bg-red-600 hover:bg-red-700 text-white border-red-600"
                            aria-label="Eliminar factura"
                          >
                            <Trash2 className="w-4 h-4" />
                          </Button>
                        </div>
                      </TableCell>
                    </TableRow>
                  );
                })}
              </TableBody>
            </Table>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

